import {
  Card,
  CardActions,
  CardContent,
  Grid,
  IconButton,
  Typography,
} from "@material-ui/core";
import { Add } from "@material-ui/icons";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import demofood from "../../demodata/demofood";
import "./foodListStyles.css";
function FoodList() {
  const { categoryId, categoryName } = useParams();
  const [foods, setFoods] = useState([]);
  useEffect(() => {
    let food_list = [];
    demofood.map((food) =>
      food.category_id == categoryId ? food_list.push(food) : null
    );
    setFoods(food_list);
  }, []);
  return (
    <>
      <br />
      <br />
      <div className="food-list-page">
        <h2 className="food-item-name" variant="h4" align="center">
          Items of {categoryName}
        </h2>
        <Grid container>
          {foods.map((food) => (
            <Grid item xs={11} sm={6} md={4} lg={3}>
              <Card className="food-item">
                <div className="food-item-image-container">
                  <img
                    className="food-item-image"
                    alt="food-item-image"
                    src={food.image}
                  />
                </div>
                <CardContent className="food-item-description">
                  <Typography variant="h6">{food.name}</Typography>
                  <Typography variant="body2" color="textSecondary">
                    {food.description}
                  </Typography>
                </CardContent>
                <CardActions className="food-item-actions">
                  <IconButton>
                    <Add />
                  </IconButton>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </div>
    </>
  );
}

export default FoodList;
