import React from "react";
import "./authStyles.css";
import { useLocation } from "react-router-dom";
import useMethods from "../../StateProvider/useMethods";
import { useForm, Controller } from "react-hook-form";
import * as Yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import { Button, TextField, Tooltip, Typography } from "@material-ui/core";
import Navigation from "../Navigation/Navigation";
import { Link } from "react-router-dom";
const scheme = Yup.object({
  email: Yup.string().email().min(4).max(100).required(),
  password: Yup.string().min(8).max(100).required(),
  confirm_password: Yup.string()
    .oneOf([Yup.ref("password"), null])
    .required(),
});
function Login() {
  const location = useLocation();
  const { login } = useMethods();
  const {
    control,
    formState: { errors },
    handleSubmit,
  } = useForm({ resolver: yupResolver(scheme) });
  const onSubmit = () => {
    login();
  };
  return (
    <>
      {location.pathname == "/login" || location.pathname == "signup" ? (
        <>
          <Navigation />
          <br />
          <br />
        </>
      ) : null}
      <div className="form-container">
        <form className="form">
          <Typography gutterBottom className="form-title" variant="h3">
            {location.pathname == "/login" ? "Login" : "Signup"}
          </Typography>
          <Controller
            name="email"
            control={control}
            render={({ field }) => (
              <TextField
                type="email"
                label="email"
                variant="outlined"
                size="small"
                {...field}
                placeholder="email..."
              />
            )}
          />
          <Typography gutterBottom variant="body2" color="secondary">
            {errors.email && errors.email}
          </Typography>
          <Controller
            name="password"
            control={control}
            render={({ field }) => (
              <TextField
                type="password"
                label="password"
                variant="outlined"
                {...field}
                placeholder="password..."
                size="small"
              />
            )}
          />

          <Typography gutterBottom variant="body2" color="secondary">
            {errors.password && errors.password}
          </Typography>

          {location.pathname == "/signup" && (
            <>
              <Controller
                name="confirm_password"
                control={control}
                render={({ field }) => (
                  <TextField
                    type="password"
                    label="confirm password"
                    variant="outlined"
                    {...field}
                    placeholder="confirm password..."
                    size="small"
                  />
                )}
              />

              <Typography gutterBottom variant="body2" color="secondary">
                {errors.confirm_password && errors.confirm_password}
              </Typography>
            </>
          )}
          <div className="form-footer">
            <Button type="submit" color="primary" variant="contained">
              {location.pathname == "/login" ? "Login" : "Signup"}
            </Button>
            <Tooltip title="click for signup">
              <Typography
                variant="body2"
                color="textSecondary"
                component={Link}
                to="/signup"
              >
                {location.pathname == "/login"
                  ? "Don't have an account?"
                  : "Already have an account?"}
              </Typography>
            </Tooltip>
          </div>
        </form>
      </div>
    </>
  );
}

export default Login;
