import React from "react";
import "./authStyles.css";
import useMethods from "../../StateProvider/useMethods";
import { useForm, Controller } from "react-hook-form";
import * as Yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import { TextField, Typography } from "@material-ui/core";
const scheme = Yup.object({
  email: Yup.string().email().min(4).max(100).required(),
  password: Yup.string().min(8).max(100).required(),
});
function Login() {
  const { login } = useMethods();
  const {
    control,
    formState: { errors },
    handleSubmit,
  } = useForm({ resolver: yupResolver(scheme) });
  const onSubmit = () => {
    login();
  };
  return (
    <div className="form-container">
      <form className="form">
        <Controller
          name="email"
          control={control}
          render={({ field }) => (
            <TextField
              type="email"
              label="email"
              variant="outlined"
              size="small"
              {...field}
              placeholder="email..."
            />
          )}
        />
        <Typography gutterBottom variant="body2" color="secondary">
          {errors.email && errors.email}
        </Typography>
        <Controller
          name="password"
          control={control}
          render={({ field }) => (
            <TextField
              type="password"
              label="password"
              variant="outlined"
              {...field}
              placeholder="password..."
            />
          )}
        />

        <Typography gutterBottom variant="body2" color="secondary">
          {errors.password && errors.password}
        </Typography>
      </form>
    </div>
  );
}

export default Login;
