import { Button, TextField, Typography, Tooltip } from "@material-ui/core";
import React, { useEffect } from "react";
import { useForm, Controller } from "react-hook-form";
import * as Yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import "./authStyles.css";
import { Link, useNavigate } from "react-router-dom";
import Navigation from "../Navigation/Navigation";
import { useStateValue } from "../../StateProvider/StateContext";
import useMethods from "../../StateProvider/useMethods";

// ===============================================
// ===============================================
// ===============================================
// ===============================================
const schema = Yup.object({
  email: Yup.string().email().required(),
  password: Yup.string().min(5).max(50).required(),
});
// ===============================================
// ===============================================
// ===============================================
// ===============================================
function Login() {
  const [state] = useStateValue();
  const navigate = useNavigate();
  const { login } = useMethods();
  const {
    control,
    formState: { errors },
    handleSubmit,
  } = useForm({ resolver: yupResolver(schema) });
  const onSubmit = (data) => {
    login(data);
  };
  // ===============================================
  // ===============================================
  // ===============================================
  // ===============================================
  useEffect(() => state.user && navigate("/"), []);
  // ===============================================
  // ===============================================
  // ===============================================
  // ===============================================
  return (
    <>
      <Navigation />
      <br />
      <br />
      <div className="form-container">
        <form className="form" onSubmit={handleSubmit(onSubmit)}>
          <Typography gutterBottom variant="h3">
            Login
          </Typography>
          <Controller
            name="email"
            control={control}
            render={({ field }) => (
              <TextField
                type="email"
                label="email"
                variant="outlined"
                size="small"
                {...field}
                placeholder="email..."
              />
            )}
          />

          <Typography gutterBottom variant="body2" color="secondary">
            {errors.email && errors.email}
          </Typography>

          <Controller
            name="password"
            control={control}
            render={({ field }) => (
              <TextField
                type="password"
                label="password"
                variant="outlined"
                size="small"
                {...field}
                placeholder="password..."
              />
            )}
          />

          <Typography gutterBottom variant="body2" color="secondary">
            {errors.password && errors.password}
          </Typography>

          <div className="form-footer">
            <Button type="submit" color="primary" variant="contained">
              Login
            </Button>
            <Tooltip title="click for signup">
              <Typography
                component={Link}
                to="/signup"
                variant="body2"
                color="textSecondary"
              >
                Don't have an account?
              </Typography>
            </Tooltip>
          </div>
        </form>
      </div>
    </>
  );
}

export default Login;
