import React, { useEffect } from "react";
import { Routes, Route } from "react-router-dom";
import Footer from "./components/Footer/Footer";
import "./styles.css";
import Categories from "./components/Categories/Categories";
import About from "./components/About/About";
import FoodList from "./components/Food/FoodList";
import FoodDescription from "./components/Food/Food Item/FoodDescription";
import Home from "./components/Home/Home";
import { useStateValue } from "./StateProvider/StateContext";
import { isExpired } from "react-jwt";
import Login from "./components/Auth/Login";
import Signup from "./components/Auth/Signup";
import Cart from "./components/Cart/Cart";
import useMethods from "./StateProvider/useMethods";
function App() {
  const [state, dispatch] = useStateValue();
  const { get_category_list } = useMethods();
  // ==========================================
  // ==========================================
  useEffect(() => {
    get_category_list();
    let token = sessionStorage.getItem("token");
    let user = sessionStorage.getItem("user");
    if (token && !isExpired(token) && user) {
      dispatch({ type: "ADD_TOKEN", token });
      dispatch({ type: "ADD_USER", user: JSON.parse(user) });
      return;
    }
    dispatch({ type: "REMOVE_TOKEN" });
    dispatch({ type: "REMOVE_USER" });
    return sessionStorage.clear();
  }, []);
  // ==========================================
  // ==========================================
  return (
    <main className="main">
      <div className="priority">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/categories" element={<Categories />} />
          <Route
            path="/food-list/:category_id/:category_name"
            element={<FoodList />}
          />
          <Route path="/about" element={<About />} />
          <Route
            path="/food-description/:foodId"
            element={<FoodDescription />}
          />

          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<Signup />} />
          <Route path="/cart" element={<Cart />} />
        </Routes>
      </div>
      <Footer />
    </main>
  );
}

export default App;
