import React from "react";
import "./authStyles.css";
import useMethods from "../../StateProvider/useMethods";
import { useForm, Controller } from "react-hook-form";
import * as Yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import { Button, TextField, Typography } from "@material-ui/core";
import { Link } from "react-router-dom";
const scheme = Yup.object({
  email: Yup.string().email().min(4).max(100).required(),
  password: Yup.string().min(8).max(100).required(),
});
function Login() {
  const { login } = useMethods();
  const {
    control,
    formState: { errors },
    handleSubmit,
  } = useForm({ resolver: yupResolver(scheme) });
  const onSubmit = () => {
    login();
  };
  return (
    <div className="form-container">
      <form className="form">
        <Typography gutterBottom className="form-title" variant="h3">
          Login
        </Typography>
        <Controller
          name="email"
          control={control}
          render={({ field }) => (
            <TextField
              type="email"
              label="email"
              variant="outlined"
              size="small"
              {...field}
              placeholder="email..."
            />
          )}
        />
        <Typography gutterBottom variant="body2" color="secondary">
          {errors.email && errors.email}
        </Typography>
        <Controller
          name="password"
          control={control}
          render={({ field }) => (
            <TextField
              type="password"
              label="password"
              variant="outlined"
              {...field}
              placeholder="password..."
              size="small"
            />
          )}
        />

        <Typography gutterBottom variant="body2" color="secondary">
          {errors.password && errors.password}
        </Typography>
        <div className="form-footer">
          <Button type="submit" color="primary" variant="contained">
            Login
          </Button>
          <Typography
            variant="body2"
            color="textSecondary"
            component={Link}
            to="/signup"
          >
            Don't have an account?
          </Typography>
        </div>
      </form>
    </div>
  );
}

export default Login;
