export const InitialState = {
  user: null,
  token: null,
  cart_id: [1],
};

export const Reducer = (state, action) => {
  switch (action.type) {
    case "ADD_USER":
      return {
        ...state,
        user: action.user,
      };
    case "REMOVE_USER":
      return {
        ...state,
        user: null,
      };
    case "ADD_TOKEN":
      return {
        ...state,
        token: action.token,
      };
    case "REMOVE_TOKEN":
      return {
        ...state,
        token: null,
      };
    case "ADD_TO_CART":
      return {
        ...state,
        cart_id: state.cart_id.unshift(action.cart_id),
      };
    case "REMOVE_FROM_CART":
      return {
        ...state,
        cart_id: state.cart_id.filter((cart) => cart != action.cart_id),
      };
    case "DELETE_CART":
      return {
        ...state,
        cart_id: [],
      };
    default:
      return state;
  }
};
