import React, { useState } from "react";
import "./authStyles.css";
import { useLocation, Link } from "react-router-dom";
import useMethods from "../../StateProvider/useMethods";

import { Button, TextField, Tooltip, Typography } from "@material-ui/core";
import Navigation from "../Navigation/Navigation";

function Authentication() {
  const [data, setData] = useState({
    username: "",
    email: "",
    password: "",
    confirm_password: "",
  });
  const [error, setError] = useState({
    username: null,
    email: null,
    password: null,
    confirm_password: null,
  });
  const location = useLocation();
  const { login, signup } = useMethods();

  const onChange = (e) => {
    setData((prevData) => ({ ...prevData, [e.target.name]: e.target.value }));
  };

  const onFormSubmit = (e) => {
    e.preventDefault();
    console.log(data);
    if (location.pathname == "/login") {
      let regex = new RegExp(/\d*@\d*\.com/);
      let emailIsValid = regex.test(data.email);
      console.log(emailIsValid);
      console.log(data.email, data.password);
    } else {
      console.log(data);
    }
    // location.pathname == "/login"
    //   ? login(email, password)
    //   : signup(username, email, password);
  };
  return (
    <>
      {location.pathname == "/login" || location.pathname == "/signup" ? (
        <>
          <Navigation />
          <br />
          <br />
        </>
      ) : null}
      <div className="form-container">
        <form onSubmit={(e) => onFormSubmit(e)} className="form">
          <Typography gutterBottom className="form-title" variant="h3">
            {location.pathname == "/login" ? "Login" : "Signup"}
          </Typography>
          {location.pathname == "/signup" && (
            <TextField
              onChange={onChange}
              name="username"
              type="text"
              label="username"
              variant="outlined"
              size="small"
              placeholder="username..."
            />
          )}

          <TextField
            onChange={onChange}
            name="email"
            type="email"
            label="email"
            variant="outlined"
            size="small"
            placeholder="email..."
          />

          <TextField
            onChange={onChange}
            name="password"
            type="password"
            label="password"
            variant="outlined"
            placeholder="password..."
            size="small"
          />

          {location.pathname == "/signup" && (
            <TextField
              onChange={onChange}
              name="confirm_password"
              type="password"
              label="confirm password"
              variant="outlined"
              placeholder="confirm password..."
              size="small"
            />
          )}
          <div className="form-footer">
            <Button type="submit" color="primary" variant="contained">
              {location.pathname == "/login" ? "Login" : "Signup"}
            </Button>
            <Tooltip title="click for signup">
              <Typography
                component={Link}
                to={location.pathname == "/login" ? "/signup" : "/login"}
                variant="body2"
                color="textSecondary"
              >
                {location.pathname == "/login"
                  ? "Don't have an account?"
                  : "Already have an account?"}
              </Typography>
            </Tooltip>
          </div>
        </form>
      </div>
    </>
  );
}

export default Authentication;
